var tempsum=0
var humsum=0
var items=msg.payload
var count = items.length
temps=items.map(function(i){return i.temperature})
hums=items.map(function(i){return i.humidity})

for (var i=0; i<count; i++){
    tempsum+=temps[i]
    humsum+=hums[i]
}

var tempavg=tempsum/count
var humavg=humsum/count
var tempmax=Math.max(...temps)
var hummax=Math.max(...hums)
var tempmin=Math.min(...temps)
var hummin=Math.min(...hums)

return {temperature_avg: tempavg, humidity_avg: humavg, 
    temperature_max: tempmax, humidity_max: hummax, 
    temperature_min: tempmin, humidity_min: hummin};

