var buf = msg.payload

if (buf.length < 2) { return null; }

var tempv = buf.readUInt16BE(9)
var humv = buf.readUInt16BE(11)
var luxv = buf.readUInt16BE(13)

var c1= -2.0468
var c2=  0.0367
var c3= -1.5955*Math.pow(10,-6)

var d1= -40
var d2=  0.01

// Compute the raw voltage on the photodiode
var luxvsensor = luxv/4096*1.5
// Compute the current through 100kOhm resistor
var luxi = luxvsensor/100000

var temp = d1 + d2*tempv
var hum = c1 + c2*humv + c3*Math.pow(humv,2)
var lux = 0.625 * Math.pow(10,6) * luxi * 1000

return {payload: {temperature: temp.toFixed(2), humidity: hum.toFixed(2), light: lux.toFixed(2)}};
